// Input-Text (z. B. "Schweinefleisch essen")
const text = $input.first().json.body.data.properties.Thema.rich_text[0].text.content || "";

// Leerzeichen durch Unterstriche ersetzen
const result = text.trim().replace(/\s+/g, "_");

return [
  {
    json: {
      original: text,
      formatted: result,
    }
  }
];
